  #{parentId,jdbcType=DECIMAL}, #{sortId,jdbcType=DECIMAL}, #{createdBy,jdbcType=DECIMAL}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updatedBy,jdbcType=DECIMAL}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(*)
from TBL_CATEGORY 
 where NAME = #{name,jdbcType=VARCHAR} and PARENT_ID = #{parentId,jdbcType=DECIMAL} and TREE_TYPE = #{treeType,jdbcType = VARCHAR}

select count(*)
from TBL_CATEGORY 
 where PARENT_ID = #{parentId,jdbcType=DECIMAL} and TREE_TYPE = #{treeType,jdbcType = VARCHAR}

select NAME
from TBL_CATEGORY 
 where OID = #{oid,jdbcType=DECIMAL}

select count(*)
from TBL_CATEGORY 
 where NAME = #{name,jdbcType=VARCHAR} and NAME != #{oldName,jdbcType=VARCHAR} and PARENT_ID = #{parentId,jdbcType=DECIMAL} and TREE_TYPE = #{treeType,jdbcType = VARCHAR}
