mapper.select:
select count(*)
from TBL_TEMPLATE_TAG
where PARENT_TAG = #{parentTagId,jdbcType=DECIMAL}

select MAX(TAG_SORT)
from TBL_TEMPLATE_TAG
where PARENT_TAG = #{parentTag,jdbcType=DECIMAL}

mapper.insert:
insert into TBL_TEMPLATE_TAG (OID, TAG_NAME, PARENT_TAG, 
  TEMPLATE_ID, TAG_SORT, CREATED_BY, 
  CREATE_TIME, UPDATED_BY, UPDATE_TIME
  )
values (#{oid,jdbcType=DECIMAL}, #{tagName,jdbcType=VARCHAR}, #{parentTag,jdbcType=DECIMAL}, 
  #{templateId,jdbcType=DECIMAL}, #{tagSort,jdbcType=DECIMAL}, #{createdBy,jdbcType=DECIMAL}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updatedBy,jdbcType=DECIMAL}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
	select count(*) from TBL_TEMPLATE_TAG

	   TEMPLATE_ID= #{templateId,jdbcType=DECIMAL}
	   AND TAG_NAME = #{tagName,jdbcType=VARCHAR}

  	AND OID != #{oid,jdbcType=DECIMAL}


  	 AND PARENT_TAG is null


  	 AND PARENT_TAG =#{parentTag,jdbcType=DECIMAL}
